        function capital_val(id)
        {
            var val = $('#'+id).val();
            val = val.charAt(0).toUpperCase() + val.slice(1);
            $('#'+id).val(val);
        }
        function chcont_type(col)
        {
            var type = '#type_'+col;
            var mid = $(type).val();
            if(mid == 'img')
            {
                $('#img_'+col).show();
                $('#text_'+col).hide();
            }
            else
            {
                $('#img_'+col).hide();
                $('#text_'+col).show();
            }
        }
